def check_for_invalid_systems
  emudeck_sytems = []
  File.readlines('emudeck-systems.txt', chomp: true).each do |line|
    emudeck_sytems << line
  end

  my_systems = SYSTEMS.map { _1[:shortname] }
  SYSTEMS.each do |system|
    if !emudeck_sytems.include?(system[:shortname])
      puts "System #{system[:name]} (#{system[:shortname]}) is not supported"
    end
  end
end
